import { Component } from '@angular/core';
import { LoginService } from '../login.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  usuario!: string;
  senha!: string;

  constructor(private _loginService: LoginService,private _router:Router){}

  fazerLogin(){
    this._loginService.login(this.usuario,this.senha);
    this._router.navigate(['/restrito/lista']);
    this._loginService.setMostraMenu(false);
  }
}
<main>
    <section id="section-login">
        <h2>Login</h2>
        <form class="row g-3">
            
              <label for="staticUsuario" class="visually-hidden">Usuário</label>
              <input type="text" name="usuario" [(ngModel)]="usuario" class="form-control" id="staticUsuario" placeholder="email@exemplo.com">
            
            <br>
            
              <label for="inputPassword2" class="visually-hidden">Senha</label>
              <input type="password" name="senha" [(ngModel)]="senha" class="form-control" id="inputPassword2" placeholder="senha">
              <br>
              <button type="submit" class="btn btn-primary mb-3" (click)="fazerLogin()">Login</button>
          
          </form>
    </section>
</main>
